import React, { Component } from "react";
import { Grid, Cell, List, ListItem, ListItemContent, Button } from "react-mdl";
import social1 from "../github.png";
import social2 from "../netlify.png";
import social3 from "../gmail.png";
import social from "../linkedin.png";

class about extends Component {
  constructor(props) {
    super(props);
    this.state = {
      clicked: false,
    };
  }

  imageClick = () => {
    console.log("Click!!!!");
    this.setState({
      clicked: true,
    });
  };
  render() {
    return (
      <div className="contact-body">
        <div>
          <img src={this.props.social1} onClick={this.imageClick} />
          {this.state.clicked && <div>You clicked me!</div>}
        </div>

        <img
          className="social2"
          src={social2}
          alt="logo...."
          onClick={() => imageClick()}
        />
        <img
          className="social3"
          src={social3}
          alt="logo...."
          onClick={() => imageClick()}
        />
        <img
          className="social"
          src={social}
          alt="logo...."
          onClick={() => imageClick()}
        />
      </div>
    );
  }
}

export default about;
